import React, { useState } from "react";
import "./css/App.css";
import namesData from "./data/namesData.json";

function App() {
  const [searchTerm, setSearchTearm] = useState("");
  const [favourites, setFavourites] = useState([]);
  return (
    <div className="App">
      <header className="App-header">
        <input
          className="App-input"
          type="text"
          placeholder="|"
          value={searchTerm}
          onChange={(event) => setSearchTearm(event.target.value)}
        />
      </header>
      <main className="App-main">
        <div className="App-main-names">
          <p className="App-main-names-title">Names</p>
          {namesData
            .sort((a, z) => a.name.localeCompare(z.name))
            .filter((nameObject) => {
              const { name } = nameObject;
              return name.toLowerCase().includes(searchTerm.toLowerCase());
            })
            .map((nameObject) => {
              const { id, name, sex } = nameObject;
              const isBoy = sex === "m";
              return (
                <div style={{ color: isBoy ? "royalblue" : "pink",padding:"5px" }}>
                  {name}
                </div>
              );
            })}
        </div>
        <div className="App-main-names">
          <p className="App-main-names-title">Favourites</p>
        </div>
      </main>
    </div>
  );
}

export default App;
